CREATE TABLE IF NOT EXISTS SUBSCRIPTIONS(id VARCHAR(64) PRIMARY KEY, "user" VARCHAR(1023) NOT NULL, contact VARCHAR(2047) NOT NULL, name VARCHAR(128), state VARCHAR(20) NOT NULL, groups VARCHAR(2000));
CREATE INDEX IF NOT EXISTS INDEX_SUBSCRIPTIONS_USER ON SUBSCRIPTIONS ("user");
CREATE UNIQUE INDEX IF NOT EXISTS UNIQUE_INDEX_SUBSCRIPTIONS_USER_CONTACT ON SUBSCRIPTIONS ("user", contact);

CREATE TABLE IF NOT EXISTS SUBSCRIPTION_NOTIFICATIONS (id VARCHAR(64) PRIMARY KEY, "user" VARCHAR(1023) NOT NULL, contact VARCHAR(2047) NOT NULL, subscription_type VARCHAR(12) NOT NULL, CONSTRAINT UNIQUE_USER_CONTACT_SUBSCRIPTION_TYPE UNIQUE ("user", contact, subscription_type));
CREATE INDEX IF NOT EXISTS INDEX_SUBSCRIPTION_NOTIFICATIONS_USER_CONTACT ON SUBSCRIPTION_NOTIFICATIONS ("user", contact);
